import React, { useState, useEffect, useRef } from 'react';
import { useLocation } from 'react-router-dom';
import './sidebar.scss';
import SidebarItem from './SidebarItem';

const sidebarNavItems = [
  { display: 'Dashboard', icon: 'bx bx-home', to: '/' },
  { display: 'Perfil', icon: 'bx bx-user', to: '/profile' },
  { display: 'Notificações', icon: 'bx bx-bell', to: '/notifications' },
  { display: 'Configurações', icon: 'bx bx-cog', to: '/settings' },
  { display: 'Sair', icon: 'bx bx-log-out', to: '/logout' },
];

const Sidebar = () => {
  const [activeIndex, setActiveIndex] = useState(0);
  const [indicatorTop, setIndicatorTop] = useState(0);
  const location = useLocation();
  const indicatorRef = useRef();

  useEffect(() => {
    const curPath = window.location.pathname.split('/')[1];
    const activeItem = sidebarNavItems.findIndex((item) => item.to === `/${curPath}`);
    setActiveIndex(curPath.length === 0 ? 0 : activeItem);
  }, [location]);

  useEffect(() => {
    const sidebarItem = document.querySelector('.sidebar__menu__item.active');
    if (sidebarItem) {
      setIndicatorTop(sidebarItem.offsetTop);
    }
  }, [activeIndex]);

  return (
    <div className='sidebar'>
      <div className="sidebar__logo">Animate</div>
      <div className="sidebar__menu">
        <div
          ref={indicatorRef}
          className="sidebar__menu__indicator"
          style={{
            top: `${indicatorTop}px`,
          }}
        ></div>
        {sidebarNavItems.map((item, index) => (
          <SidebarItem
            key={index}
            to={item.to}
            icon={item.icon}
            display={item.display}
            active={index === activeIndex}
          />
        ))}
      </div>
    </div>
  );
};

export default Sidebar;
