import * as React from 'react';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardActionArea from '@mui/material/CardActionArea';
import Typography from '@mui/material/Typography';
import Grid from '@mui/material/Grid';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import { styled } from '@mui/system';

const theme = createTheme();

const HoverCard = styled(Card)({
  width: '60%',
  margin: 'auto', // Centraliza o card na tela
  marginBottom: '20px', // Adiciona um pequeno espaÃ§amento entre os cards
  transition: 'box-shadow 0.3s, background-color 0.3s',
  position: 'relative',
  '&:hover': {
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
    backgroundColor: 'rgba(0, 0, 0, 0.8)',
    '& .hover-message': {
      opacity: 1,
    },
    '& img': {
      filter: 'brightness(50%)', // Aplica um filtro de brilho para escurecer a imagem
    },
  },
});

const HoverMessage = styled(Typography)({
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  opacity: 0,
  transition: 'opacity 0.3s',
  color: 'white',
  textAlign: 'center',
  fontFamily: 'Arial, sans-serif', // Especifica uma fonte mais comum
  fontSize: '1.2rem', // Tamanho da fonte aumentado
});

const ClientDashboard = () => {
  return (
    <ThemeProvider theme={theme}>
      <Grid container justifyContent="center" alignItems="center" style={{ height: '100vh' }}>
        <Grid item xs={12} sm={5} sx={{ marginRight: 2 }}>
          <HoverCard>
            <CardActionArea>
              <img
                src="https://via.placeholder.com/400x250"
                alt="OrÃ§amento"
                style={{ width: '100%', height: 'auto', transition: 'filter 0.3s' }}
              />
            </CardActionArea>
            <HoverMessage className="hover-message" variant="body2">
              Clique para criar um novo orÃ§amento
            </HoverMessage>
            <CardContent>
              <Typography gutterBottom variant="h5" component="div" sx={{ color: 'white' }}>
                Fazer OrÃ§amento
              </Typography>
              <Typography variant="body1" color="text.secondary" sx={{ color: 'white' }}>
                Crie um novo orÃ§amento para os produtos ou serviÃ§os desejados. Adicione detalhes especÃ­ficos e revise antes de enviar.
              </Typography>
            </CardContent>
          </HoverCard>
        </Grid>

        <Grid item xs={12} sm={5}>
          <HoverCard>
            <CardActionArea>
              <img
                src="https://via.placeholder.com/400x250"
                alt="Novo Pedido"
                style={{ width: '100%', height: 'auto', transition: 'filter 0.3s' }}
              />
            </CardActionArea>
            <HoverMessage className="hover-message" variant="body2">
              Clique para fazer um novo pedido
            </HoverMessage>
            <CardContent>
              <Typography gutterBottom variant="h5" component="div" sx={{ color: 'white' }}>
                Fazer Novo Pedido
              </Typography>
              <Typography variant="body1" color="text.secondary" sx={{ color: 'white' }}>
                FaÃ§a um novo pedido com facilidade e rapidez. Escolha os produtos desejados, especifique as quantidades e conclua o processo em alguns passos simples.
              </Typography>
            </CardContent>
          </HoverCard>
        </Grid>
      </Grid>
    </ThemeProvider>
  );
};

export default ClientDashboard;
