import React, { useState } from 'react';
import './sidebar.scss';
import SidebarItem from './SidebarItem';
import { FaHome, FaUser, FaBell, FaCog, FaSignOutAlt } from 'react-icons/fa';

const sidebarNavItems = [
  { icon: <FaHome />, to: '/' },
  { icon: <FaUser />, to: '/profile' },
  { icon: <FaBell />, to: '/notifications' },
  { icon: <FaCog />, to: '/settings' },
  { icon: <FaSignOutAlt />, to: '/logout' },
];

const Sidebar = () => {
  const [activeIndex, setActiveIndex] = useState(0);
  const [isCollapsed, setIsCollapsed] = useState(false);
  const sidebarRef = React.useRef();
  const indicatorRef = React.useRef();

  const toggleCollapse = () => {
    setIsCollapsed(!isCollapsed);
  };
    return (
      <div className={`sidebar ${isCollapsed ? 'collapsed' : ''}`}>
      <div className="sidebar__logo" onClick={toggleCollapse}>
        Animate
      </div>
      <div ref={sidebarRef} className="sidebar__menu">
        <div
          ref={indicatorRef}
          className="sidebar__menu__indicator"
          style={{
            transform: `translateX(-50%) translateY(${activeIndex * 60}px)`,
          }}
        ></div>
        {sidebarNavItems.map((item, index) => (
          <SidebarItem
            key={index}
            to={item.to}
            icon={item.icon}
            active={activeIndex === index}
          />
        ))}
      </div>
    </div>
  );
};


export default Sidebar;
