import CircularProgress from '@mui/material/CircularProgress';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import Container from '@mui/material/Container';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import { useHistory } from 'react-router-dom';
import React, { useEffect } from 'react';

const theme = createTheme();

function LoadingScreen() {
    useEffect(() => {
        const timer = setTimeout(() => {
            // Redirecionar para a tela desejada (por exemplo, tela principal)
            history.push('/tela-desejada');
        }, 6000); // Tempo em milissegundos (por exemplo, 3000ms = 3 segundos)

        return () => clearTimeout(timer); // Limpar o temporizador ao desmontar o componente
    }, []);

    return (
        <ThemeProvider theme={theme}>
            <Container
                component="main"
                maxWidth="xs"
                sx={{
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'center',
                    justifyContent: 'center',
                    height: '100vh',
                }}
            >
                <Box>
                    <CircularProgress />
                </Box>
                <Typography component="h1" variant="h5" sx={{ mt: 2 }}>
                    Carregando...
                </Typography>
            </Container>
        </ThemeProvider>
    );
}

export default LoadingScreen;
