import * as React from 'react';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardActionArea from '@mui/material/CardActionArea';
import Typography from '@mui/material/Typography';
import Grid from '@mui/material/Grid';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import { styled } from '@mui/system';

const theme = createTheme();

const HoverCard = styled(Card)({
  width: '60%',
  margin: 'auto', // Centraliza o card na tela
  marginBottom: '20px', // Adiciona um pequeno espaçamento entre os cards
  transition: 'box-shadow 0.3s, background-color 0.3s',
  position: 'relative',
  '&:hover': {
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
    backgroundColor: 'rgba(0, 0, 0, 0.7)',
    '& .hover-message': {
      opacity: 1,
    },
  },
});

const HoverMessage = styled(Typography)({
  position: 'absolute',
  bottom: '20px',
  left: '50%',
  transform: 'translateX(-50%)',
  opacity: 0,
  transition: 'opacity 0.3s',
  color: 'white',
});

const ClientDashboard = () => {
  return (
    <ThemeProvider theme={theme}>
      <Grid container justifyContent="center" alignItems="center" style={{ height: '100vh' }}>
        <Grid item xs={12} sm={5} sx={{ marginRight: 2 }}>
          <HoverCard>
            <CardActionArea>
              <img
                src="https://via.placeholder.com/400x250"
                alt="Orçamento"
                style={{ width: '100%', height: 'auto' }}
              />
              <CardContent>
                <Typography gutterBottom variant="h5" component="div">
                  Fazer Orçamento
                </Typography>
                <Typography variant="body1" color="text.secondary">
                  Crie um novo orçamento para os produtos ou serviços desejados. Adicione detalhes específicos e revise antes de enviar.
                </Typography>
              </CardContent>
            </CardActionArea>
            <HoverMessage className="hover-message" variant="body2">
              Clique para criar um novo orçamento
            </HoverMessage>
          </HoverCard>
        </Grid>

        <Grid item xs={12} sm={5}>
          <HoverCard>
            <CardActionArea>
              <img
                src="https://via.placeholder.com/400x250"
                alt="Novo Pedido"
                style={{ width: '100%', height: 'auto' }}
              />
              <CardContent>
                <Typography gutterBottom variant="h5" component="div">
                  Fazer Novo Pedido
                </Typography>
                <Typography variant="body1" color="text.secondary">
                  Faça um novo pedido com facilidade e rapidez. Escolha os produtos desejados, especifique as quantidades e conclua o processo em alguns passos simples.
                </Typography>
              </CardContent>
            </CardActionArea>
            <HoverMessage className="hover-message" variant="body2">
              Clique para fazer um novo pedido
            </HoverMessage>
          </HoverCard>
        </Grid>
      </Grid>
    </ThemeProvider>
  );
};

export default ClientDashboard;
