import React, { useState, useLayoutEffect, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import './sidebar.scss';
import SidebarItem from './SidebarItem';

const sidebarNavItems = [
  { display: 'Dashboard', icon: 'bx bx-home', to: '/' },
  { display: 'Perfil', icon: 'bx bx-user', to: '/profile' },
  { display: 'Notificações', icon: 'bx bx-bell', to: '/notifications' },
  { display: 'Configurações', icon: 'bx bx-cog', to: '/settings' },
  { display: 'Sair', icon: 'bx bx-log-out', to: '/logout' },
];

const Sidebar = () => {
  const [activeIndex, setActiveIndex] = useState(0);
  const [stepHeight, setStepHeight] = useState(0);
  const sidebarRef = React.useRef();
  const indicatorRef = React.useRef();
  const location = useLocation();

  useLayoutEffect(() => {
    const sidebarItem = sidebarRef.current.querySelector('.sidebar__menu__item');
    if (sidebarItem) {
      indicatorRef.current.style.height = `${sidebarItem.clientHeight}px`;
      setStepHeight(sidebarItem.clientHeight);
      // Posiciona o indicador no primeiro item do menu
      indicatorRef.current.style.transform = `translateX(-50%) translateY(${activeIndex * stepHeight}px)`;
    }
  }, [activeIndex, stepHeight]);

  useEffect(() => {
    const curPath = window.location.pathname.split('/')[1];
    const activeItem = sidebarNavItems.findIndex((item) => item.to === `/${curPath}`);
    setActiveIndex(curPath.length === 0 ? 0 : activeItem);
  }, [location]);

  const getClassName = (index) => (index === activeIndex ? 'active' : '');

  return (
    <div className='sidebar'>
      <div className="sidebar__logo">Animate</div>
      <div ref={sidebarRef} className="sidebar__menu">
        <div
          ref={indicatorRef}
          className="sidebar__menu__indicator"
        ></div>
        {sidebarNavItems.map((item, index) => (
          <SidebarItem
            key={index}
            to={item.to}
            icon={item.icon}
            display={item.display}
            active={getClassName(index)}
          />
        ))}
      </div>
    </div>
  );
};

export default Sidebar;
