import * as React from 'react';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardActionArea from '@mui/material/CardActionArea';
import Typography from '@mui/material/Typography';
import Grid from '@mui/material/Grid';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import { styled } from '@mui/system';

const theme = createTheme();

const HoverCard = styled(Card)({
  width: '80%',
  margin: 'auto',
  marginBottom: '20px',
  borderRadius: '15px',
  background: 'linear-gradient(to right, #1976D2, #64B5F6)',
  transition: 'transform 0.3s, box-shadow 0.3s',
  position: 'relative',
  '&:hover': {
    transform: 'scale(1.05)',
    boxShadow: '0 8px 16px rgba(0, 0, 0, 0.7)',
    '& .hover-message': {
      opacity: 1,
    },
    '& img': {
      filter: 'brightness(50%)',
    },
  },
});

const HoverMessage = styled(Typography)({
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  opacity: 0,
  transition: 'opacity 0.3s',
  color: 'white',
  textAlign: 'center',
  fontFamily: 'Arial, sans-serif',
  fontSize: '1.5rem',
});

const ClientDashboard = () => {
  return (
    <ThemeProvider theme={theme}>
      <Grid container justifyContent="center" alignItems="center" style={{ minHeight: '100vh' }}>
        <Grid item xs={12} sm={6} md={4}>
          <HoverCard>
            <CardActionArea>
              <img
                src="https://via.placeholder.com/150"
                alt="Orçamento"
                style={{ width: '100%', height: 'auto', borderRadius: '15px 15px 0 0', transition: 'filter 0.3s' }}
              />
              <HoverMessage className="hover-message" variant="body2">
                Solicitar Orçamento
              </HoverMessage>
            </CardActionArea>
            <CardContent>
              <Typography gutterBottom variant="h5" component="div" sx={{ color: 'white' }}>
                Criar Orçamento
              </Typography>
              <Typography variant="body1" color="text.secondary" sx={{ color: 'white' }}>
                Gere um novo orçamento para os produtos ou serviços desejados. Adicione detalhes específicos e revise antes de enviar.
              </Typography>
            </CardContent>
          </HoverCard>
        </Grid>

        <Grid item xs={12} sm={6} md={4}>
          <HoverCard>
            <CardActionArea>
              <img
                src="https://via.placeholder.com/150"
                alt="Novo Pedido"
                style={{ width: '100%', height: 'auto', borderRadius: '15px 15px 0 0', transition: 'filter 0.3s' }}
              />
              <HoverMessage className="hover-message" variant="body2">
                Criar Novo Pedido
              </HoverMessage>
            </CardActionArea>
            <CardContent>
              <Typography gutterBottom variant="h5" component="div" sx={{ color: 'white' }}>
                Fazer Novo Pedido
              </Typography>
              <Typography variant="body1" color="text.secondary" sx={{ color: 'white' }}>
                Faça um novo pedido com facilidade e rapidez. Escolha os produtos desejados, especifique as quantidades e conclua o processo em alguns passos simples.
              </Typography>
            </CardContent>
          </HoverCard>
        </Grid>
      </Grid>
    </ThemeProvider>
  );
};

export default ClientDashboard;
