// Sidebar.js
import React, { useEffect, useRef, useState, useLayoutEffect } from 'react';
import { useLocation } from 'react-router-dom';
import './sidebar.scss';
import SidebarItem from './SidebarItem';

const sidebarNavItems = [
    {
      display: 'Dashboard',
      icon: <i className='bx bx-home'></i>,
      to: '/',
      section: ''
    },
    {
      display: 'Perfil',
      icon: <i className='bx bx-user'></i>,
      to: '/profile',
      section: 'profile'
    },
    {
      display: 'Notificações',
      icon: <i className='bx bx-bell'></i>,
      to: '/notifications',
      section: 'notifications'
    },
    {
      display: 'Configurações',
      icon: <i className='bx bx-cog'></i>,
      to: '/settings',
      section: 'settings'
    },
    {
      display: 'Sair',
      icon: <i className='bx bx-log-out'></i>,
      to: '/logout',
      section: 'logout'
    },
  ];

const Sidebar = () => {
  const [activeIndex, setActiveIndex] = useState(0);
  const [stepHeight, setStepHeight] = useState(0);
  const sidebarRef = useRef();
  const indicatorRef = useRef();
  const location = useLocation();

  useLayoutEffect(() => {
    const sidebarItem = sidebarRef.current.querySelector('.sidebar__menu__item');
    indicatorRef.current.style.height = `${sidebarItem.clientHeight}px`;
    setStepHeight(sidebarItem.clientHeight);
  }, []);

  useEffect(() => {
    const curPath = window.location.pathname.split('/')[1];
    const activeItem = sidebarNavItems.findIndex(item => item.section === curPath);
    setActiveIndex(curPath.length === 0 ? 0 : activeItem);
  }, [location]);

  const getClassName = (index) => (activeIndex === index ? 'active' : '');

  return (
    <div className='sidebar'>
      <div className="sidebar__logo">Animate</div>
      <div ref={sidebarRef} className="sidebar__menu">
        <div
          ref={indicatorRef}
          className="sidebar__menu__indicator"
          style={{
            transform: `translateX(-50%) translateY(${activeIndex * stepHeight}px)`
          }}
        ></div>
        {sidebarNavItems.map((item, index) => (
          <SidebarItem
            key={index}
            to={item.to}
            icon={item.icon}
            display={item.display}
            active={getClassName(index)}
          />
        ))}
      </div>
    </div>
  );
};

export default Sidebar;
